update item_sales_propery
property_value_id = #{itemSalesPropery.propertyValueId}, property_actual_value = #{itemSalesPropery.propertyActualValue}, picture = #{itemSalesPropery.picture}, update_time = #{itemSalesPropery.updateTime}, is_deleted = #{itemSalesPropery.isDeleted}
where spu_code = #{itemSalesPropery.spuCode} AND property_id = #{itemSalesPropery.propertyId}
