update.foreach:
update skus
property_value_id = #{skus.propertyValueId}, property_value = #{skus.propertyValue}, bar_code = #{skus.barCode}, market_price = #{skus.marketPrice}, picture = #{skus.picture}, weight = #{skus.weight}, update_time = #{skus.updateTime}, is_valid = #{skus.isValid}, is_deleted = #{skus.isDeleted}, sku_name = #{skus.skuName}, spec_info = #{skus.specInfo}
where spu_code = #{skus.spuCode} AND sku_code = #{skus.skuCode}

mapper.select:
SELECT sk.sku_name AS skuName, sk.sku_code AS skuCode, sk.spu_code AS spuCode, sk.spec_info AS specInfo, sk.bar_code AS barCode, sk.item_id AS itemId FROM skus sk LEFT JOIN (SELECT it.spu_code,br.name brandName FROM items it LEFT JOIN brand br ON it.brand_id = br.id) its ON sk.spu_code = its.spu_code WHERE sk.is_valid = 1
AND its.brandName = #{brandName}
AND sk.sku_name LIKE CONCAT('%', #{skuName}, '%')
AND sk.sku_code LIKE CONCAT('%', #{skuCode}, '%')
AND sk.spu_code LIKE CONCAT('%', #{spuCode}, '%')
AND sk.sku_code NOT IN
#{item}
LIMIT #{start},#{pageSize}

SELECT count(*) FROM ( SELECT sk.sku_name AS skuName, sk.sku_code AS skuCode, sk.spu_code AS spuCode, sk.spec_info AS specInfo, sk.bar_code AS barCode, sk.item_id AS itemId FROM skus sk LEFT JOIN (SELECT it.spu_code,br.name brandName FROM items it LEFT JOIN brand br ON it.brand_id = br.id) its ON sk.spu_code = its.spu_code WHERE sk.is_valid = 1
AND its.brandName = #{brandName}
AND sk.sku_name LIKE CONCAT('%', #{skuName}, '%')
AND sk.sku_code LIKE CONCAT('%', #{skuCode}, '%')
AND sk.spu_code LIKE CONCAT('%', #{spuCode}, '%')
AND sk.sku_code NOT IN
#{item}
) t
