mapper.select:
SELECT s.supplier_name AS supplierName, pao.purchase_order_code AS purchaseOrderCode, pao. STATUS, pao.create_time AS submitTime, pao.create_operator AS createOperator, pao.purchase_type AS purchaseType, pao.purchase_group_code AS purchaseGroupCode, pao.contract_code AS contractCode, pao.warehouse_code AS warehouseCode, pao.requried_receive_date AS requriedReceiveDate, pao.end_receive_date AS endReceiveDate, pao.id FROM supplier s RIGHT JOIN ( SELECT ao.purchase_order_code, ao. STATUS, ao.create_time, ao.create_operator, po.purchase_type, po.supplier_code, po.purchase_group_code, po.contract_code, po.warehouse_code, po.requried_receive_date, po.end_receive_date, po.id FROM apply_for_purchase_order ao LEFT JOIN purchase_order po ON ao.purchase_order_code = po.purchase_order_code WHERE ao.is_deleted = '0'
AND ao.purchase_order_code LIKE CONCAT('%', #{purchaseOrderCode},'%')
AND ao.STATUS = #{auditStatus}
AND ao.create_time > #{startDate}
AND ao.create_time < #{startDate}
AND po.purchase_type = #{purchaseType}
AND po.channel_code = #{channelCode}
) pao ON s.supplier_code = pao.supplier_code
WHERE s.supplier_name LIKE CONCAT('%', #{supplierName},'%')
order BY submitTime DESC

SELECT COUNT(*) FROM supplier s RIGHT JOIN ( SELECT ao.purchase_order_code, ao. STATUS, ao.create_time, ao.create_operator, po.purchase_type, po.supplier_code, po.purchase_group_code, po.contract_code, po.warehouse_code, po.requried_receive_date, po.end_receive_date FROM apply_for_purchase_order ao LEFT JOIN purchase_order po ON ao.purchase_order_code = po.purchase_order_code WHERE ao.is_deleted = '0'
AND ao.purchase_order_code LIKE CONCAT('%', #{purchaseOrderCode},'%')
AND ao.STATUS = #{auditStatus}
AND ao.create_time > #{startDate}
AND ao.create_time < #{startDate}
AND po.purchase_type = #{purchaseType}
AND po.channel_code = #{channelCode}
) pao ON s.supplier_code = pao.supplier_code
WHERE s.supplier_name LIKE CONCAT('%', #{supplierName},'%')
