mapper.select:
SELECT s.supplier_name AS supplierName, pao.purchase_order_code AS purchaseOrderCode, pao.STATUS, pao.create_time AS createTime, pao.create_operator AS createOperator, pao.purchase_type AS purchaseType, pao.contract_code AS contractCode, pao.warehouse_code AS warehouseCode, pao.requried_receive_date AS requriedReceiveDate, pao.end_receive_date AS endReceiveDate, pao.warehouse_notice_code AS warehouseNoticeCode, pao.id FROM supplier s RIGHT JOIN ( SELECT ao.purchase_order_code, ao.warehouse_notice_code, ao.STATUS, ao.create_time, ao.create_operator, ao.purchase_type, ao.supplier_code, ao.contract_code, ao.warehouse_code, ao.requried_receive_date, ao.end_receive_date, ao.id FROM warehouse_notice ao LEFT JOIN purchase_order po ON ao.purchase_order_code = po.purchase_order_code WHERE ao.id != ''
AND ao.warehouse_notice_code LIKE CONCAT('%', #{warehouseNoticeCode},'%')
AND ao.purchase_order_code LIKE CONCAT('%', #{purchaseOrderCode},'%')
AND ao.STATUS = #{status}
AND ao.create_time > #{startDate}
AND ao.create_time < #{endDate}
AND ao.purchase_type = #{purchaseType}
AND po.channel_code = #{channelCode}
) pao ON s.supplier_code = pao.supplier_code
WHERE s.supplier_name LIKE CONCAT('%', #{supplierName},'%')
order BY status ASC

SELECT COUNT(*) FROM supplier s RIGHT JOIN ( SELECT ao.purchase_order_code, ao. STATUS, ao.create_time, ao.create_operator, ao.purchase_type, ao.supplier_code, ao.contract_code, ao.warehouse_code, ao.requried_receive_date, ao.end_receive_date, ao.id FROM warehouse_notice ao LEFT JOIN purchase_order po ON ao.purchase_order_code = po.purchase_order_code WHERE ao.id != ''
AND ao.warehouse_notice_code LIKE CONCAT('%', #{warehouseNoticeCode},'%')
AND ao.purchase_order_code LIKE CONCAT('%', #{purchaseOrderCode},'%')
AND ao.STATUS = #{status}
AND ao.create_time > #{startDate}
AND ao.create_time < #{endDate}
AND ao.purchase_type = #{purchaseType}
AND po.channel_code = #{channelCode}
) pao ON s.supplier_code = pao.supplier_code
WHERE s.supplier_name LIKE CONCAT('%', #{supplierName},'%')
